const replyMsgTypes = new ReplyMsgTypes();

/**
* Signals
*/
class ReqSignals {
    constructor() {
        this._start = 1;
        this._startSignal = 'start';
        this._continue = 2;
        this._continueSignal = 'continue';
        this._end = 3;
        this._endSignal = 'end';
        this._idle = 4;
        this._idleSignal = 'idle';
        this._quit = 5;
        this._quitSignal = 'quit';
        this._passedFromCustomerService = 6;
        this._passedFromCustomerServiceSignal = 'passed_from_customer_service';
    }

    get start() { return this._start; }
    get startSignal() { return this._startSignal; }
    get continue() { return this._continue; }
    get continueSignal() { return this._continueSignal; }
    get end() { return this._end; }
    get endSignal() { return this._endSignal; }
    get idle() { return this._idle; }
    get idleSignal() { return this._idleSignal; }
    get quit() { return this._quit; }
    get quitSignal() { return this._quitSignal; }
    get passedFromCustomerService() { return this._passedFromCustomerService; }
    get passedFromCustomerServiceSignal() { return this._passedFromCustomerServiceSignal; }


    isStart(val) {
        return val === this.start || (typeof val === 'string' && val.toLowerCase() === this.startSignal);
    }
    isEnd(val) {
        return val === this.end || (typeof val === 'string' && val.toLowerCase() === this.endSignal);
    }
    isContinue(val) {
        return val === this.continue || (typeof val === 'string' && val.toLowerCase() === this.continueSignal);
    }
    isIdle(val) {
        return val === this.idle || (typeof val === 'string' && val.toLowerCase() === this.idleSignal);
    }
    isQuit(val) {
        return val === this.quit || (typeof val === 'string' && val.toLowerCase() === this.quitSignal);
    }
    isPassedFromCustomerService(val) {
        return val === this.passedFromCustomerService || (typeof val === 'string' && val.toLowerCase() === this.passedFromCustomerServiceSignal);
    }
}

const reqSignals = new ReqSignals();

/**
 * Controller for request channels
 */
class ReqChannels {
    constructor() {
    }

    /**
     * Channel object with default name `text`
     *
     * @readonly
     * @memberof ReqChannels
     */
    get text() {
        return new Channel('文字', { isText: true, isSpeech: false });
    }

    /**
     * Channel object of default name `speech`
     *
     * @readonly
     * @memberof ReqChannels
     */
    get speech() {
        return new Channel('語音', { isText: false, isSpeech: true });
    }

    /**
     * If the chanel supports message type of `text`
     * @param {Channel} val Channel object
     */
    isText(val) {
        return !node._.isNil(val) && val.isText;
    }

    /**
     * If the channel supports message type of `speech`
     * @param {Channel} val Channel object
     */
    isSpeech(val) {
        return !node._.isNil(val) && val.isSpeech;
    }
}

const reqChannels = new ReqChannels();

/**
 * Channel object
 * @property {string} name
 * @property {boolean} isText if this channel supports text messages
 * @property {boolean} isSpeech if this channel 
 */
class Channel {
    constructor(nameOrChannel, configs) {
        if (!node._.isNil(nameOrChannel) &&
            node._.isString(nameOrChannel.name) &&
            node._.isBoolean(nameOrChannel.isText) && node._.isBoolean(nameOrChannel.isSpeech)) {
            this.name = nameOrChannel.name;
            this.isText = nameOrChannel.isText;
            this.isSpeech = nameOrChannel.isSpeech;
        }
        if (node._.isString(nameOrChannel)) {
            this.name = nameOrChannel;
        }
        if (!node._.isNil(configs)) {
            this.isText = configs.isText || false;
            this.isSpeech = configs.isSpeech || false;
        }
    }

    valueOf() {
        return this.name;
    }

    toString() {
        return this.name;
    }
}

class ReplyActionTypes {
    constructor() {
        this._grading = 'grading';
        this._end = 'end';
        this._renew = 'renew';
    }

    get grading() { return this._grading; }
    get end() { return this._end; }
    get renew() { return this._renew; }

    isGrading(val) {
        return val === this.grading;
    }
    isEnd(val) {
        return val === this.end;
    }
    isRenew(val) {
        return val === this._renew;
    }
}

const replyActionTypes = new ReplyActionTypes();

/**
* Reply message factory
* recommand to use `create()`
*/
class ReplyMsgFactory {
    createText(text) {
        if (node._.isString(text)) {
            return {
                type: replyMsgTypes.text,
                text: text
            };
        }
        return null;
    }
    createTextType(text) {
        if (node._.isString(text)) {
            return {
                type: replyMsgTypes.textType,
                text: text
            };
        }
        return null;
    }
    createSpeech(speech) {
        if (node._.isString(speech)) {
            return {
                type: replyMsgTypes.speech,
                speech: speech
            };
        }
        return null;
    }
    createSpeechType(speech) {
        if (node._.isString(speech)) {
            return {
                type: replyMsgTypes.speechType,
                speech: speech
            };
        }
        return null;
    }
    createImage(title, alt, imgUrl) {
        return {
            type: replyMsgTypes.image,
            title: title,
            alt: alt,
            imgUrl: imgUrl
        };
    }
    /**
     * 建立圖片類型訊息
     * @param {*} params { title, alt, imgUrl }
     */
    createImageType(params) {
        let msg = {
            type: replyMsgTypes.imageType
        };
        if (params) {
            if (params.title) {
                msg.title = params.title;
            }
            if (params.alt) {
                msg.alt = params.alt;
            }
            if (params.imgUrl) {
                msg.imgUrl = params.imgUrl;
            }
        }
        return msg;
    }
    createSticker(alt, stickerUrl) {
        return {
            type: replyMsgTypes.sticker,
            alt: alt,
            imgUrl: stickerUrl
        };
    }
    /**
     * 建立貼圖類型訊息
     * @param {*} params { alt, imgUrl }
     */
    createStickerType(params) {
        let msg = {
            type: replyMsgTypes.stickerType
        };
        if (params) {
            if (params.alt) {
                msg.alt = params.alt;
            }
            if (params.imgUrl) {
                msg.imgUrl = params.imgUrl;
            }
        }
        return msg;
    }
    createLink(title, url, data) {
        let reply = {
            type: replyMsgTypes.link,
            text: title, // TODO: deprecated
            title: title,
            url: url
        };
        reply = Object.assign(reply, data);
        return reply;
    }
    /**
     * 建立連結類型訊息
     * @param {*} params { title, url }
     */
    createLinkType(params) {
        let reply = {
            type: replyMsgTypes.linkType
        };
        if (params) {
            if (params.title) {
                reply.title = params.title;
            }
            if (params.url) {
                reply.url = params.url;
            }
        }
        return reply;
    }
    createDataList(data, title) {
        return {
            type: replyMsgTypes.dataList,
            title: title,
            data: Array.isArray(data) ? data : [data]
        };
    }
    /**
     * 建立 data list 類型訊息
     * @param {*} params { data, title, botType, intent }
     */
    createDataListType(params) {
        let reply = {
            type: replyMsgTypes.dataListType
        };
        if (params) {
            if (Array.isArray(params.data) && params.data.length) {
                reply.data = node._.cloneDeep(params.data);
            }
            if (params.title) {
                reply.title = params.title;
            }
            if (params.botType) {
                reply.botType = params.botType;
            }
            if (params.intent) {
                reply.intent = params.intent;
            }
        }
        return reply;
    }
    createYesOrNo(yesText, noText) {
        return {
            type: replyMsgTypes.yesOrNo,
            yes: yesText,
            no: noText
        };
    }
    /**
     * 建立 yes/no 選項訊息
     * @param {*} params { yes, no }
     */
    createYesOrNoType(params) {
        let reply = {
            type: replyMsgTypes.yesOrNoType
        };
        if (params) {
            if (params.yes) {
                reply.yes = params.yes;
            }
            if (params.no) {
                reply.no = params.no;
            }
        }
        return reply;
    }
    createToCustomerService(options) {
        let reply = {
            type: replyMsgTypes.toCustomerService
        };
        if (node._.isObject(options)) {
            for (let prop in options) {
                reply[prop] = options[prop];
            }
        }
        return reply;
    }
    createToCustomerServiceType(options) {
        let reply = {
            type: replyMsgTypes.toCustomerServiceType
        };
        if (node._.isObject(options)) {
            for (let prop in options) {
                reply[prop] = options[prop];
            }
        }
        return reply;
    }
    createToOnlineService(options) {
        let reply = {
            type: replyMsgTypes.toOnlineService
        };
        if (node._.isObject(options)) {
            for (let prop in options) {
                reply[prop] = options[prop];
            }
        }
        return reply;
    }
    createToOnlineServiceType(options) {
        let reply = {
            type: replyMsgTypes.toOnlineServiceType
        };
        if (node._.isObject(options)) {
            for (let prop in options) {
                reply[prop] = options[prop];
            }
        }
        return reply;
    }
    createToAuthorization(options) {
        let reply = {
            type: replyMsgTypes.toAuthorization,
        };
        if (node._.isObject(options)) {
            for (let prop in options) {
                reply[prop] = options[prop];
            }
        }
        return reply;
    }
    createToAuthorizationType(options) {
        let reply = {
            type: replyMsgTypes.toAuthorizationType
        };
        if (node._.isObject(options)) {
            for (let prop in options) {
                reply[prop] = options[prop];
            }
        }
        return reply;
    }
    createToLocation(botType, entities, location) {
        return {
            type: replyMsgTypes.toLocation,
            botType: botType,
            location: location,
            entities: entities
        };
    }
    /**
     * 建立需取得定位訊息
     * @param {*} params { botType, intent }
     */
    createToLocationType(params) {
        let reply = {
            type: replyMsgTypes.toLocationType
        };
        if (params) {
            if (params.botType) {
                reply.botType = params.botType;
            }
            if (params.intent) {
                reply.intent = params.intent;
            }
        }
        return reply;
    }
    createRedirectBot(opts) {
        return {
            type: replyMsgTypes.redirectBot,
            botType: opts && opts.botType,
            intent: opts && opts.intent,
            entities: opts && opts.entities,
            data: opts && opts.data
        };
    }
    /**
     * 建立要求總機重新導向 second bot 類型訊息
     * @param {*} params { botType, intent, entities, data }
     */
    createRedirectBotType(params) {
        let reply = {
            type: replyMsgTypes.redirectBotType
        };
        if (params) {
            if (params.botType) {
                reply.botType = params.botType;
            }
            if (params.intent) {
                reply.intent = params.intent;
            }
            if (params.entities) {
                reply.entities = node._.cloneDeep(params.entities);
            }
            if (params.data) {
                reply.data = node._.cloneDeep(params.data);
            }
        }
        return reply;
    }
    /**
     * 建立 options 訊息物件
     * data 陣列中僅允許包含 text, speech, image, sticker, link
     * @param opts <Option_Item>[] Including properties : title, payload, url
     * @param title string Title
     */
    createOptions(opts, title) {
        const ctrl = new ReplyMsgCtrl();
        let options = Array.isArray(opts) ? [].concat(opts) : [opts];
        options = options.filter(opt => {
            return !!opt && (opt.title && (opt.payload || opt.url));
        });

        let reply = {
            type: replyMsgTypes.options
        };
        if (options) {
            reply.data = options;
        }
        if (title) {
            reply.title = title;
        }
        return reply;
    }
    /**
     * 建立選項類型訊息
     * @param {*} params { opts, title }
     */
    createOptionsType(params) {
        let reply = {
            type: replyMsgTypes.optionsType
        };

        if (params) {
            let options = Array.isArray(params.opts) ? [].concat(opts) : [opts];
            options = options.filter(opt => {
                return !!opt && (opt.title && (opt.payload || opt.url));
            });


            if (options) {
                reply.data = node._.cloneDeep(options);
            }
            if (title) {
                reply.title = title;
            }
        }
        return reply;
    }
    createStatus(code, msg) {
        return {
            type: replyMsgTypes.status,
            status: {
                code: code,
                message: msg
            }
        };
    }
    /**
     * 建立狀態訊息
     * @param {*} params { status: { code, message } }
     */
    createStatusType(params) {
        let reply = {
            type: replyMsgTypes.statusType
        };
        if (params) {
            if (params.status) {
                reply.status = node._.cloneDeep(params.status);
            }
        }
        return reply;
    }
    createAction(actionType) {
        return {
            type: replyMsgTypes.action,
            action: actionType
        };
    }
    /**
     * 建立動作訊息
     * @param {*} params { action }
     */
    createActionType(params) {
        let reply = {
            type: replyMsgTypes.actionType
        };
        if (params) {
            if (params.action) {
                reply.action = params.action;
            }
        }
        return reply;
    }
    create(data) {
        const reply = {};
        if (data.type) { reply.type = data.type; }
        if (data.text) { reply.text = data.text; }
        if (data.speech) { reply.speech = data.speech; }
        if (data.title) { reply.title = data.title; }
        if (data.alt) { reply.alt = data.alt; }
        if (data.imgUrl) { reply.imgUrl = data.imgUrl; }
        if (data.url) { reply.url = data.url; }
        if (data.data) { reply.data = node._.cloneDeep(data.data); }
        if (data.yes) { reply.yes = data.yes; }
        if (data.no) { reply.no = data.no; }
        if (data.userId) { reply.userId = data.userId; }
        if (data.botType) { reply.botType = data.botType; }
        if (data.intent) { reply.intent = data.intent; }
        if (data.entities) { reply.entities = node._.cloneDeep(data.entities); }
        if (data.location) { reply.location = node._.cloneDeep(data.location); }
        if (data.action) { reply.action = data.action; }
        if (data.status) { reply.status = node._.cloneDeep(data.status); }
        if (data.postbackReq) { reply.postbackReq = node._.cloneDeep(data.postbackReq); }
        return reply;
    }
}

