    /**
     * Normalize lang code: lower case & separated by `-`
     * ex: `zh-tw`, `en-us`
     * @param {string} langCode lang code
     */
    normalize(langCode) {
        if (!!langCode) {
            return langCode.toLowerCase().trim();
        }
    }

    /**
     * Return the `langCode`, if it's valid in `validLangs`, `defaultLang` otherwise 
     * @param {string[]} validLangs collection of valid lang codes
     * @param {string} langCode lang code to be determined
     * @param {string} defaultLang default as `langCode` not in `validLangs`
     */
    validOrDefault(validLangs, langCode, defaultLang) {
        if (Array.isArray(validLangs)) {
            return validLangs.some(x => this.normalize(x) === this.normalize(langCode)) ?
                langCode : defaultLang;
        }
    }
}

class AnalysisCtrl {
    constructor() { }

    /**
     * Map NLU result for analysis
     * 
     * @param {Object} nlu NLU result object
     * @param {Object} extraConfigs Extra configs for NLU  
     * ```typescript
     * {
     *   isFirstNLU: boolean;
     *   isSecondNLU: boolean;
     *   isThirdNLU: boolean;
     *   threshold: number;
     * }
     * ```
     * @returns NLU Result for analysis
     * ```typescript
     * {
     *   sentence: string;
     *   intents: IntentResult[];
     *   entities: EntityResult[];
     * 
     *   isFirstNLU: boolean;
     *   isSecondNLU: boolean;
     *   isThirdNLU: boolean;
     *   threshold: number;
     *   secondThreshold: number;
     *   fallbackScore: number;
     *   name: string;
     * }
     * ```
     */
    mapNLUResult(nlu, extraConfigs) {
        let res = null;
        if (!node._.isNil(nlu)) {
            res = {};
            res.sentence = nlu.sentence;
            res.intents = [].concat(nlu.intents.map(x => this.mapIntentResult(x)).filter(x => !!x));
            res.entities = [].concat(nlu.entities.map(y => this.mapEntityResult(y)).filter(y => !!y));

            res.isFirstNLU = !!extraConfigs && extraConfigs.isFirstNLU || false;
            res.isSecondNLU = !!extraConfigs && extraConfigs.isSecondNLU || false;
            res.isThirdNLU = !!extraConfigs && extraConfigs.isThirdNLU || false;
            res.threshold = !!extraConfigs && extraConfigs.threshold || 0;
            res.secondThreshold = !!extraConfigs && extraConfigs.secondThreshold || 0;
            res.fallbackScore = !!extraConfigs && extraConfigs.fallbackScore || 0;
            res.name = !!extraConfigs && extraConfigs.name;
        }
        return res;
    }

    mapIntentResult(intent) {
        let res = null;
        if (!node._.isNil(intent)) {
            res = {};
            res.intent_id = intent.intent_id;
            res.intent = intent.intent;
            res.score = intent.score;
        }
        return res;
    }

    mapEntityResult(entity) {
        let res = null;
        if (!node._.isNil(entity)) {
            res = {};
            res.entity_id = entity.entity_id;
            res.info = entity.info;
            res.text = entity.text;
            res.key_user_phrase = entity.key_user_phrase;
            res.entity = entity.entity;
        }
        return res;
    }

    /**
     * Map FAQ result for analysis
     *
     * @param {Object} faq FAQ Result object
     * @param {Object} extraConfigs Extra configs for FAQ
     * ```typescript
     * {
     *   isFirstFAQ: boolean;
     *   threshold: number;
     *   secondThreshold: number;
     *   name: string;
     * }
     * ```
     */
    mapFAQResult(faq, extraConfigs) {
        let res = null;
        if (!node._.isNil(faq)) {
            res = {};
            res.qid = faq.id;
            res.originalQuestion = faq.orig_question;
            res.answer = faq.answer;

            if (!node._.isNil(faq.multichannel_answers) && Array.isArray(faq.multichannel_answers)) {
                res.multichannel_answers = [].concat(faq.multichannel_answers.map(x => this.mapMultiChannelFAQAnswers(x)));
            }
            res.categories = [].concat(node._.cloneDeep(faq.categories));
            res.score = faq.score;

            res.threshold = !!extraConfigs && extraConfigs.threshold || 0;
            res.secondThreshold = !!extraConfigs && extraConfigs.secondThreshold || 0;
            res.isFirstFAQ = !!extraConfigs && extraConfigs.isFirstFAQ || false;
            res.name = !!extraConfigs && extraConfigs.name;
        }
        return res;
    }

    mapMultiChannelFAQAnswers(ans) {
        let res = null;
        if (!node._.isNil(ans)) {
            res = {};
            res.channel = ans.tab.name;
            res.answer = ans.answer;
        }
        return res;
    }

    /**
     * Map recored NLU result for Report used
     * @param {Object} nluResult 
     */
    mapNLUResultForReport(nluResult) {
        if (!!nluResult &&
            Array.isArray(nluResult.intents) && nluResult.intents.length) {

            let isUndecided = false;
            let isSuggested = false;
            if (node._.isNumber(nluResult.threshold)) {
                isUndecided = isUndecided || nluResult.intents[0].score < nluResult.threshold;
            }
            if (node._.isNumber(nluResult.fallbackScore)) {
                isUndecided = isUndecided || nluResult.intents[0].score === nluResult.fallbackScore;
            }
            if (node._.isNumber(nluResult.secondThreshold)) {
                isSuggested = isSuggested || nluResult.intents[0].score >= nluResult.secondThreshold;
            }

            return {
                intent: nluResult.intents[0].intent,
                undecided: isUndecided,
                suggested: isSuggested
            };
        }
        return null;
    }

    /**
     * Map recored FAQ result for Report used
     * @param {Object} faqResult 
     * @param {Object} extraConfigs Extra configs for `faqResult`
     * ```typescript
     * {
     *   parentCategory: string;
     *   childCategory: string;
     * }
     * ```
     */
    mapFAQResultForReport(faqResult, extraConfigs) {
        let res = null;
        if (!!faqResult) {
            res = {};
            res.qid = faqResult.qid;
            res.originalQuestion = faqResult.originalQuestion;
            res.score = faqResult.score;
            res.undecided = node._.isNumber(faqResult.threshold) ? faqResult.score < faqResult.threshold : false;
            res.suggested = node._.isNumber(faqResult.secondThreshold) ? faqResult.score >= faqResult.secondThreshold : false;
            res.parentCategory = extraConfigs.parentCategory || undefined;
            res.childCategory = extraConfigs.childCategory || undefined;
            return res;
        }
        return null;
    }

    /**
     * Get the NLU result marked as the first NLU
     * @param {Object[]} list 
     */
    getFirstNLU(list) {
        if (Array.isArray(list) && list.length) {
            return list.find(x => x.isFirstNLU) || null;
        }
    }

    /**
     * Get the NLU result marked as the second NLU
     * @param {Object[]} list 
     */
    getSecondNLU(list) {
        if (Array.isArray(list) && list.length) {
            return list.find(x => x.isSecondNLU) || null;
        }
    }

    /**
     * Get the NLU result marked as the third NLU
     * @param {Object[]} list
     */
    getThirdNLU(list) {
        if (Array.isArray(list) && list.length) {
            return list.find(x => x.isThirdNLU) || null;
        }
    }

    /**
     * Get the FAQ result marked as the first FAQ
     * @param {Object[]} list 
     */
    getFirstFAQ(list) {
        if (Array.isArray(list) && list.length) {
            return list.find(x => x.isFirstFAQ) || null;
        }
    }
}

/**
