class ChatWebRequest {
    constructor () {
        this.channel = msg.reqMsg.message.channel,
        this.source = msg.config.source,
        this.role = msg.config.role,
        this.content = [];
    }

    get getMsgObj () {
        return this;
    }
    addScene (code) {
        this.content.push({scene: code});
    }
    addTextCode (obj) {
        if (typeof obj === 'string') {
            this.content.push({textCode: obj});
        } else if (typeof obj === 'object') {
            this.content.push({textCode: obj.textCode, dictionary: obj.dictionary});
        }
    }
}

