const ReplyMsgFactory = msg.systalk.utils.ReplyMsgFactory;

msg.messages.push(ReplyMsgFactory.createText('謝謝您'));

msg.quit = true;
msg._end_dialogue = true;
return msg;

const ReqMsgCtrl = msg.systalk.utils.ReqMsgCtrl;
const LangCtrl = msg.systalk.utils.LangCtrl;
const ReqChannels = msg.systalk.utils.ReqChannels;


// normalize request message
ReqMsgCtrl.normalize(msg.reqMsg);
// 進線 channel
msg.systalk.channel = node._.clone(msg.reqMsg.channel || ReqChannels.text);
// 使用者語系
msg.systalk.lang = LangCtrl.normalize(msg.reqMsg.lang || msg.systalk.lang || 'zh-tw');




// 判斷是否清空紀錄 session 資訊
if (msg.systalk.sessionSalt) {
    if (msg.systalk.sessionSalt !== msg.reqMsg.sessionSalt) {
        msg.systalk.needInfo = undefined;
        msg.systalk.config = undefined;
        msg.systalk.pendingBot = undefined;
    }
}
msg.systalk.sessionSalt = msg.reqMsg.sessionSalt;
msg.systalk.fromBot = msg.config.botTypes.current;

if (!msg.systalk.needInfo) {
    msg.systalk.needInfo = {
        flags: {}
    };
}

// setup config values
msg.systalk.config = msg.reqMsg.config || msg.systalk.config || null;
if (!msg.systalk.config) {
    msg.systalk.config = {};
}

let inputIntent = node._.isString(msg.reqMsg.intent) ?
    msg.reqMsg.intent :
    !!msg.reqMsg.intent ? msg.reqMsg.intent.item : null;
msg.botType = inputIntent || msg.systalk.pendingBot || msg.config.botTypes.current;
msg.messages = [];
return msg;

msg.reqMsg = msg.payload;
msg.systalk._session_id = msg.reqMsg.sessionId;
msg._start_setup = true;
return msg;

/**
 * This file define utils for Demo Kit and template.
 * Could be copied into a function node or import as function library (current not working).
 * 
 * Define in `msg.systalk.utils`
 * 定義在 `function node`，會使用到 `Lodash`（定義在 `node._`）
 */

/**
 * Request message types
 * 總機與各 bot 的 Request 訊息類別
 */
class ReqMsgTypes {
    constructor() {
        this._text = 1;
        this._textType = 'text';
        this._speech = 2;
        this._speechType = 'speech';
        this._fromOnlineService = 3;
        this._fromOnlineServiceType = 'from_online_service';
        this._fromAuthorization = 4;
        this._fromAuthorizationType = 'from_authorization';
        this._fromLocation = 5;
        this._fromLocationType = 'from_location';
        this._signal = 6;
        this._signalType = 'signal';
        this._config = 7;
        this._configType = 'config';
        this._grade = 8;
        this._gradeType = 'grade';
        this._initSession = 9;
        this._initSessionType = 'init_session';
    }
    get text() { return this._text; }
    get textType() { return this._textType }
    get speech() { return this._speech; }
    get speechType() { return this._speechType; }
    get fromOnlineService() { return this._fromOnlineService; }
    get fromOnlineServiceType() { return this._fromOnlineServiceType; }
    get fromAuthorization() { return this._fromAuthorization; }
    get fromAuthorizationType() { return this._fromAuthorizationType; }
    get fromLocation() { return this._fromLocation; }
    get fromLocationType() { return this._fromLocationType; }
    get signal() { return this._signal; }
    get signalType() { return this._signalType; }
    get config() { return this._config; }
    get configType() { return this._configType; }
    get grade() { return this._grade; }
    get gradeType() { return this._gradeType; }
    get initSession() { return this._initSession; }
    get initSessionType() { return this._initSessionType; }

    isText(val) {
        return val === this.text || val === this.textType;
    }
    isSpeech(val) {
        return val === this.speech || val === this.speechType;
    }
    isFromOnlineService(val) {
        return val === this.fromOnlineService || val === this.fromOnlineServiceType;
    }
    isFromAuthorization(val) {
        return val === this.fromAuthorization || val === this.fromAuthorizationType;
    }
    isFromLocation(val) {
        return val === this.fromLocation || val === this.fromLocationType;
    }
    isSignal(val) {
        return val === this.signal || val === this.signalType;
    }
    isConfig(val) {
        return val === this.config || val === this.configType;
    }
    isGrade(val) {
        return val === this.grade || val === this.gradeType;
    }
    isInitSession(val) {
        return val === this.initSession || val === this.initSessionType;
    }
}

const reqMsgTypes = new ReqMsgTypes();

