class LocalesUtils {

    /**
     * 取得 locales 對應語句或物件
     * 若指定 channel 不存在 key 值，判斷 text 或 speech 是否有定義
     * 
     * @param {Channel} channel Channel object
     * @param {string | Array} key 以 dot ('.')分隔的字串，表示 property path
     * @param {Object} replaceParams
     */
    get(locales, channel, key, replaceParams) {
        if (!node._.isNil(locales)) {
            let result = node._.get(locales[channel], key);
            // 預設 Channel 與對應的語料
            if (!result) {
                if (reqChannels.isText(channel)) {
                    result = node._.get(locales[reqChannels.text], key);
                }
                else if (reqChannels.isSpeech(channel)) {
                    result = node._.get(locales[reqChannels.speech], key);
                }
            }
            if (!!result && typeof result === 'string') {
                result = this.replaceTextByParams(result, replaceParams);
            }
            return result;
        }
    }

    /**
     * 取代文字中指定參數，文字中須以 {{param}} 表示
     * @param {*} text 
     * @param {*} params 取代參數值，會包含對應的參數，如 { param: "new value" }
     */
    replaceTextByParams(text, params) {
        if (params) {
            Object.keys(params).forEach(key => {
                if (typeof params[key] === 'string') {
                    const regex = new RegExp('{{\\s*' + key + '\\s*}}', 'gm');
                    text = text.replace(regex, params[key]);
                }
            });
        }
        return text;
    }
}

/**
 * 提供 First-In-First-Out 操作於指定的 array
 */
class QueueUtils {
    /**
     * 新增至 Queue 最後
     */
    push(queue, data) {
        if (Array.isArray(queue) && !!data) {
            if (!node._.isEqual(this.last(queue), data))
                queue.push(data);
        }
    }
    /**
     * 從 Queue 最後取出
     */
    pop(queue) {
        if (Array.isArray(queue)) {
            let item = queue.pop();
            return typeof item === 'object' ?
                node._.cloneDeep(item) : item;
        }
        return null;
    }
    /**
     * 從 Queue 第一個取出
     */
    shift(queue) {
        if (Array.isArray(queue)) {
            let item = queue.shift();
            return typeof item === 'object' ?
                node._.cloneDeep(item) : item;
        }
        return null;
    }
    /**
     * 清除 Queue
     */
    clear(queue) {
        if (Array.isArray(queue)) {
            queue.splice(0, queue.length);
        }
    }
    /**
     * 刪除 Queue 中指定項目
     */
    remove(queue, index) {
        if (Array.isArray(queue) &&
            typeof index === 'number' && index >= 0 &&
            queue.length && index < queue.length) {
            queue.splice(index, 1);
        }
    }
    /**
     * 取得 Queue 的第一個項目
     */
    first(queue) {
        if (Array.isArray(queue) && queue.length > 0) {
            return typeof queue[0] === 'object' ?
                Object.assign({}, queue[0]) : queue[0];
        }
        return null;
    }
    /**
     * 取得 Queue 的最新的項目
     */
    last(queue) {
        if (Array.isArray(queue) && queue.length > 0) {
            return typeof queue[queue.length - 1] === 'object' ?
                Object.assign({}, queue[queue.length - 1]) : queue[queue.length - 1];
        }
        return null;
    }

    /**
     * 取得指定順位的項目
     * @param {*} queue array of items as queue
     * @param {*} index 0-based index
     */
    get(queue, index) {
        if (Array.isArray(queue) &&
            typeof index === 'number' && index >= 0 &&
            queue.length && index < queue.length) {
            return typeof queue[index] === 'object' ?
                Object.assign({}, queue[index]) : queue[index];
        }
    }
    /**
     * 從最後一個倒數，取得指定順位的項目
     * @param {*} queue array of items as queue
     * @param {*} index 0-based index
     */
    getFromLast(queue, index) {
        if (Array.isArray(queue) &&
            typeof index === 'number' && index >= 0 &&
            queue.length && index < queue.length) {
            return typeof queue[queue.length - index - 1] === 'object' ?
                Object.assign({}, queue[queue.length - index - 1]) : queue[queue.length - index - 1];
        }
    }
}

/**
 * 字串相關工具
 */
class StringUtils {
    /**
     * 計算字串最短距離
     * @param {*} str1 
     * @param {*} str2 
     */
    levenshteinDistance(str1, str2) {
        str1 = str1 || '';
        str2 = str2 || '';
        let vectorPrevious = [];
        let vectorCurrent = [];

        for (let i = 0; i <= str2.length; ++i) {
            vectorPrevious[i] = i;
        }
        for (let i = 0; i < str1.length; ++i) {
            vectorCurrent[0] = i + 1;

            for (let j = 0; j < str2.length; ++j) {
                let deletionCost = vectorPrevious[j + 1] + 1;
                let insertionCost = vectorCurrent[j] + 1;
                let substitutionCost = 0;

                if (str1[i] === str2[j]) {
                    substitutionCost = vectorPrevious[j];
                } else {
                    substitutionCost = vectorPrevious[j] + 1;
                }
                vectorCurrent[j + 1] = node._.min([
                    deletionCost,
                    insertionCost,
                    substitutionCost
                ]);
            }

            vectorPrevious = [].concat(vectorCurrent);
            vectorCurrent = [];
        }
        return vectorPrevious[str2.length];
    }

    /**
     * 計算字串最短距離，並轉成相似度(0.0 ~ 1.0)
     * @param {*} str1 
     * @param {*} str2 
     */
    stringSimilarity(str1, str2) {
        let distance = this.levenshteinDistance(str1, str2);
        let maxLen = node._.max([str1.length, str2.length]);
        return parseFloat((maxLen - distance) / maxLen);
    }
}

const stringUtils = new StringUtils();

/**
 * Setup and update `msg.report_stats` and `msg.systalk.report_stats_session`
 */
class ReportUtils {

    /**
     * Get channel's description, then set to `msg.report_stats_session.channel`
     * @param {Object} reportStatsSession report stats per session
     * @param {Channel} channel Channel object
     */
    setSessionChannel(reportStatsSession, channel) {
        if (!node._.isNil(reportStatsSession) && !node._.isNil(channel)) {
            let stats = reportStatsSession;
            stats.channel = channel.toString();
        }
    }

    /**
     * 
     * @param {Object} reportStatsSession report stats per session
     * @param {number} hasInputed `true` if user has inputed
     */
    setSessionUserHasInputed(reportStatsSession, hasInputed) {
        if (!node._.isNil(reportStatsSession)) {
            reportStatsSession.userHasInputed = hasInputed || false;
        }
    }

    /**
     * Set `msg.systalk.report_stats_session.toCustomerService` to check if this session is transfered to customer service
     * @param {Object} reportStatsSession report stats per session
     * @param {boolean} isTransferedToCS is this session transfered to customer service
     */
    setSessionToCustomerService(reportStatsSession, isTransferedToCS) {
        reportStatsSession.toCustomerService = node._.isNil(isTransferedToCS) ? false : isTransferedToCS;
    }

    /**
     * 使用者對 Chat Bot 評分
     * @param {*} reportStatsSession report stats per session
     * @param {number} score 評分
     */
    setSessionScoreForBot(reportStatsSession, score) {
        if (!node._.isNil(reportStatsSession)) {
            let _score = parseInt(score);
            reportStatsSession.botUserScore = !isNaN(_score) && _score >= 0 ? _score : 0;
        }
    }

    /**
     * 使用者對客服人員的評分
     * @param {Object} reportStatsSession report stats per session
     * @param {number} score 評分
     */
    setSessionScoreForAgent(reportStatsSession, score) {
        if (!node._.isNil(reportStatsSession)) {
            let _score = parseInt(score);
            reportStatsSession.agentUserScore = !isNaN(_score) && _score >= 0 ? _score : 0;
        }
    }

    /**
     * 設定是否為 `undecided`
     * @param {Object} reportStats report stats per message
     * @param {string} deciding `Decided`, `Undecided` or `Suggested FAQ`
     */
    setDeciding(reportStats, deciding) {
        if (!node._.isNil(reportStats) && !node._.isNil(deciding)) {
            reportStats.deciding = deciding;
        }
    }
}

class LangCtrl {
    constructor() {
    }

    /**
     * Normalize lang code: lower case & separated by `-`
     * ex: `zh-tw`, `en-us`
     * @param {string} langCode lang code
     */
    normalize(langCode) {
        if (!!langCode) {
            return langCode.toLowerCase().trim();
        }
    }

    /**
     * Return the `langCode`, if it's valid in `validLangs`, `defaultLang` otherwise 
     * @param {string[]} validLangs collection of valid lang codes
     * @param {string} langCode lang code to be determined
     * @param {string} defaultLang default as `langCode` not in `validLangs`
     */
    validOrDefault(validLangs, langCode, defaultLang) {
        if (Array.isArray(validLangs)) {
            return validLangs.some(x => this.normalize(x) === this.normalize(langCode)) ?
                langCode : defaultLang;
        }
    }
}

class AnalysisCtrl {
    constructor() { }

    /**
     * Map NLU result for analysis
     * 
     * @param {Object} nlu NLU result object
     * @param {Object} extraConfigs Extra configs for NLU  
     * ```typescript
     * {
     *   isFirstNLU: boolean;
     *   isSecondNLU: boolean;
     *   isThirdNLU: boolean;
     *   threshold: number;
     * }
     * ```
     * @returns NLU Result for analysis
     * ```typescript
     * {
     *   sentence: string;
     *   intents: IntentResult[];
     *   entities: EntityResult[];
     * 
     *   isFirstNLU: boolean;
     *   isSecondNLU: boolean;
     *   isThirdNLU: boolean;
     *   threshold: number;
     *   secondThreshold: number;
     *   fallbackScore: number;
     *   name: string;
     * }
     * ```
     */
    mapNLUResult(nlu, extraConfigs) {
        let res = null;
        if (!node._.isNil(nlu)) {
            res = {};
            res.sentence = nlu.sentence;
            res.intents = [].concat(nlu.intents.map(x => this.mapIntentResult(x)).filter(x => !!x));
            res.entities = [].concat(nlu.entities.map(y => this.mapEntityResult(y)).filter(y => !!y));

            res.isFirstNLU = !!extraConfigs && extraConfigs.isFirstNLU || false;
            res.isSecondNLU = !!extraConfigs && extraConfigs.isSecondNLU || false;
            res.isThirdNLU = !!extraConfigs && extraConfigs.isThirdNLU || false;
            res.threshold = !!extraConfigs && extraConfigs.threshold || 0;
            res.secondThreshold = !!extraConfigs && extraConfigs.secondThreshold || 0;
            res.fallbackScore = !!extraConfigs && extraConfigs.fallbackScore || 0;
            res.name = !!extraConfigs && extraConfigs.name;
        }
        return res;
    }

    mapIntentResult(intent) {
        let res = null;
        if (!node._.isNil(intent)) {
            res = {};
            res.intent_id = intent.intent_id;
            res.intent = intent.intent;
            res.score = intent.score;
        }
        return res;
    }

    mapEntityResult(entity) {
        let res = null;
        if (!node._.isNil(entity)) {
            res = {};
            res.entity_id = entity.entity_id;
            res.info = entity.info;
            res.text = entity.text;
            res.key_user_phrase = entity.key_user_phrase;
            res.entity = entity.entity;
        }
        return res;
    }

    /**
     * Map FAQ result for analysis
     *
     * @param {Object} faq FAQ Result object
     * @param {Object} extraConfigs Extra configs for FAQ
     * ```typescript
     * {
     *   isFirstFAQ: boolean;
     *   threshold: number;
     *   secondThreshold: number;
     *   name: string;
     * }
     * ```
     */
    mapFAQResult(faq, extraConfigs) {
        let res = null;
        if (!node._.isNil(faq)) {
            res = {};
            res.qid = faq.id;
            res.originalQuestion = faq.orig_question;
            res.answer = faq.answer;

            if (!node._.isNil(faq.multichannel_answers) && Array.isArray(faq.multichannel_answers)) {
                res.multichannel_answers = [].concat(faq.multichannel_answers.map(x => this.mapMultiChannelFAQAnswers(x)));
            }
            res.categories = [].concat(node._.cloneDeep(faq.categories));
            res.score = faq.score;

            res.threshold = !!extraConfigs && extraConfigs.threshold || 0;
            res.secondThreshold = !!extraConfigs && extraConfigs.secondThreshold || 0;
            res.isFirstFAQ = !!extraConfigs && extraConfigs.isFirstFAQ || false;
            res.name = !!extraConfigs && extraConfigs.name;
        }
        return res;
    }

    mapMultiChannelFAQAnswers(ans) {
        let res = null;
        if (!node._.isNil(ans)) {
            res = {};
            res.channel = ans.tab.name;
            res.answer = ans.answer;
        }
        return res;
    }

    /**
     * Map recored NLU result for Report used
     * @param {Object} nluResult 
     */
    mapNLUResultForReport(nluResult) {
        if (!!nluResult &&
            Array.isArray(nluResult.intents) && nluResult.intents.length) {

            let isUndecided = false;
            let isSuggested = false;
            if (node._.isNumber(nluResult.threshold)) {
                isUndecided = isUndecided || nluResult.intents[0].score < nluResult.threshold;
            }
            if (node._.isNumber(nluResult.fallbackScore)) {
                isUndecided = isUndecided || nluResult.intents[0].score === nluResult.fallbackScore;
            }
            if (node._.isNumber(nluResult.secondThreshold)) {
                isSuggested = isSuggested || nluResult.intents[0].score >= nluResult.secondThreshold;
            }

            return {
                intent: nluResult.intents[0].intent,
                undecided: isUndecided,
                suggested: isSuggested
            };
        }
        return null;
    }

    /**
     * Map recored FAQ result for Report used
     * @param {Object} faqResult 
     * @param {Object} extraConfigs Extra configs for `faqResult`
     * ```typescript
     * {
     *   parentCategory: string;
     *   childCategory: string;
     * }
     * ```
     */
    mapFAQResultForReport(faqResult, extraConfigs) {
        let res = null;
        if (!!faqResult) {
            res = {};
            res.qid = faqResult.qid;
            res.originalQuestion = faqResult.originalQuestion;
            res.score = faqResult.score;
            res.undecided = node._.isNumber(faqResult.threshold) ? faqResult.score < faqResult.threshold : false;
            res.suggested = node._.isNumber(faqResult.secondThreshold) ? faqResult.score >= faqResult.secondThreshold : false;
            res.parentCategory = extraConfigs.parentCategory || undefined;
            res.childCategory = extraConfigs.childCategory || undefined;
            return res;
        }
        return null;
    }

    /**
     * Get the NLU result marked as the first NLU
     * @param {Object[]} list 
     */
    getFirstNLU(list) {
        if (Array.isArray(list) && list.length) {
            return list.find(x => x.isFirstNLU) || null;
        }
    }

    /**
     * Get the NLU result marked as the second NLU
     * @param {Object[]} list 
     */
    getSecondNLU(list) {
        if (Array.isArray(list) && list.length) {
            return list.find(x => x.isSecondNLU) || null;
        }
    }

    /**
     * Get the NLU result marked as the third NLU
     * @param {Object[]} list
     */
    getThirdNLU(list) {
        if (Array.isArray(list) && list.length) {
            return list.find(x => x.isThirdNLU) || null;
        }
    }

    /**
     * Get the FAQ result marked as the first FAQ
     * @param {Object[]} list 
     */
    getFirstFAQ(list) {
        if (Array.isArray(list) && list.length) {
            return list.find(x => x.isFirstFAQ) || null;
        }
    }
}

/**
 * 提供 `msg.config` 相關 Utility 方法
 */
class ConfigCtrl {

    getBotApi(name) {
        if (name && msg.config.apis.bots) {
            return msg.config.apis.bots[name];
        }
    }

    getNLUApi(name) {
        if (name && msg.config.apis && msg.config.apis.nlu) {
            return msg.config.apis.nlu[name];
        }
    }

    getFAQApi(name) {
        if (name && msg.config.apis && msg.config.apis.faq) {
            return msg.config.apis.faq[name];
        }
    }

    getLocalesApi() {
        if (msg.config.apis && msg.config.apis.locales) {
            return msg.config.apis.locales;
        }
    }

    /**
     * 取得 NLU 門檻值
     * @param {string} name 設定的 NLU 名稱
     */
    getNLUThreshold(name) {
        if (name && msg.config.threshold && msg.config.threshold.nlu) {
            return msg.config.threshold.nlu[name];
        }
    }

    /**
     * 取得 NLU 次門檻值
     * @param {string} name 設定的 NLU 名稱
     */
    getNLUSecondThreshold(name) {
        return this.getNLUMinorThreshold(name);
    }

    /**
     * 取得 NLU 次門檻值
     * @param {string} name 設定的 NLU 名稱
     */
    getNLUMinorThreshold(name) {
        if (name && msg.config.secondThreshold && msg.config.secondThreshold.nlu) {
            return msg.config.secondThreshold.nlu[name];
        }
    }

    /**
     * 取得 FAQ 門檻值
     * @param {string} name 設定 FAQ 的名稱
     */
    getFAQThreshold(name) {
        if (name && msg.config.threshold && msg.config.threshold.faq) {
            return msg.config.threshold.faq[name];
        }
    }

    /**
     * 取得 FAQ 次門檻值
     * @param {string} name 設定的 FAQ 名稱
     */
    getFAQSecondThreshold(name) {
        return this.getFAQMinorThreshold(name);
    }

    /**
     * 取得 FAQ 次門檻值
     * @param {string} name 設定的 FAQ 名稱
     */
    getFAQMinorThreshold(name) {
        if (name && msg.config.secondThreshold && msg.config.secondThreshold.faq) {
            return msg.config.secondThreshold.faq[name];
        }
    }

    /**
     * 取得原點值
     * @param {string} name 設定 NLU 的名稱
     */
    getFallbackScore(name) {
        return this.getOriginValue(name);
    }

    /**
     * 取得原點值
     * @param {string} name 設定 NLU 的名稱
     */
    getOriginValue(name) {
        if (name && msg.config.nluFallbackScore) {
            return msg.config.nluFallbackScore[name];
        }
    }

    /**
     * service 是否擁有指定的操作意圖（與項目意圖）
     * @param { string } serviceName 服務意圖名稱
     * @param { string } operationName 操作意圖名稱
     * @param { item } itemName 項目意圖名稱
     */
    serviceHasOperationAndItem(serviceName, operationName, itemName) {
        if (!msg.config.intentsMap || !serviceName) {
            return false;
        }

        const operations = msg.config.intentsMap[serviceName] && Object.keys(msg.config.intentsMap[serviceName]) || [];
        let items = [];
        operations.forEach(op => {
            items = items.concat(msg.config.intentsMap[serviceName][op] || []);
        });

        const hasOperation = operationName && operations.includes(operationName);
        const hasItem = itemName && items.includes(itemName);
        const itemsOfOperation = msg.config.intentsMap[serviceName] && Array.isArray(msg.config.intentsMap[serviceName][operationName]) ?
            msg.config.intentsMap[serviceName][operationName] : [];
        const hasItemOfOperation = operationName && itemName && hasOperation &&
            itemsOfOperation.includes(itemName);

        return hasOperation || hasItem || hasItemOfOperation;
    }
}


