class BotUtils {
    replaceTextByParams(text, params) {
        if (params) {
            Object.keys(params).forEach(key => {
                if (params[key]) {
                    const regex = new RegExp('{{\\s*' + key + '\\s*}}', 'gm');
                    text.replace(regex, params[key]);
                }
            });
        }
        return text;
    }
    createYesNo () {
        return {
            yes:{
                values:  msg.config.correctText,
                threshold: 0.7
            },
            no: {
                values: msg.config.wrongText,
                threshold: 0.7
            }
        }
    }
    /**
     * 現在時間是否介於 
     * 早安: 04:01~11:00
     * 午安: 11:01~17:00
     * 晚安: 17:01~04:00
     */
    getCurrentTimePeriod () {
        const currentDate = new Date();
        const currentMoment = node.moment(currentDate).tz("Asia/Taipei");
        const currentHour = currentMoment.hour();
        let timePeriod = ['早安', '午安', '晚安'];
        let currentTimePeriod = timePeriod.find(elem=> {
            let endMoment = node.moment(currentDate).tz("Asia/Taipei");
            let startMoment = node.moment(currentDate).tz("Asia/Taipei");
            if (elem === '早安') {
                endMoment.set('hour', 11);
                endMoment.set('minute', 0);
                endMoment.set('second', 0);

                startMoment.set('hour', 4);
                startMoment.set('minute', 1);
                startMoment.set('second', 0);
            } else if (elem === '午安') {
                endMoment.set('hour', 17);
                endMoment.set('minute', 0);
                endMoment.set('second', 0);

                startMoment.set('hour', 11);
                startMoment.set('minute', 1);
                startMoment.set('second', 0);
            } 
            // else {
            //     endMoment.set('hour', 4);
            //     endMoment.set('minute', 0);
            //     endMoment.set('second', 0);

            //     startMoment.set('hour', 17);
            //     startMoment.set('minute', 1);
            //     startMoment.set('second', 0);
            // }
            return currentMoment.diff(startMoment) >= 0 && currentMoment.diff(endMoment) <= 0;

        })
        node.log('currentTimePeriod ' + currentTimePeriod);
        return currentTimePeriod || '晚安';
    }
}
