// Flow create requestBody to gateWay
class ChatWebRequest {
    constructor () {
        this.channel = msg.reqMsg.message.channel,
        this.source = msg.config.source,
        this.role = msg.config.role,
        this.content = [];
    }

    get getMsgObj () {
        return this;
    }
    addScene (code) {
        this.content.push({scene: code});
    }
    addTextCode (obj) {
        if (typeof obj === 'string') {
            this.content.push({textCode: obj});
        } else if (typeof obj === 'object') {
            this.content.push({textCode: obj.textCode, dictionary: obj.dictionary});
        }
    }
}

// Flow response to Vue
class ChatWebResponse {
    constructor () {
        this.channel = msg.reqMsg.message.channel,
        this.source = msg.config.source,
        this.role = msg.config.role,
        this.content = [];
    }

    get getMsgObj () {
        const date = new Date();
        return {
            code: 0,
            msg: '成功',
            data: {
                channel: this.channel,
                source: this.source,
                role: this.role,
                content: this.content,
                showtime: node.moment(date).tz("Asia/Taipei").format("YYYY/MM/DD HH:mm:ss")
            }
        }
    }
    addText (str) {
        this.content.push({type:1, text: str});
    }
    addQuicklyReply (obj) {
        this.content.push ({ 
            type:7,
            linkListType:"select-tag" + obj.length.toString(),
            linkList:obj
        })
    }
    // obj ex:
    // [
