    //         text:"信用卡資料(限正卡)",
    //         data:"信用卡資料(限正卡)"
    //     }
    // ]


    addModal (name) {
        this.content.push({
            type: 8,
            widget: name
        })
    }
    addGuessYouWant (cards, width = "segment secard") {
        let arr = [];
        cards.forEach(card=>{
