const otpInfo = msg.systalk.needInfo.otp;
msg.payload = {
    source: '01',
    custId: otpInfo.custId,
    birthday: msg.systalk.needInfo.birthday,
    seqNo: otpInfo.seqNo,
    otpCode: msg.reqMsg.message.param.data.otpCode
}
msg.url = msg.config.apis.gateWay.checkOtp;
return msg;

const ChatWebRequest = msg.systalk.utils.ChatWebRequest;

if(msg.payload.code !== 0){//查詢失敗
    ChatWebRequest.addTextCode('');
    msg.reqBody = ChatWebRequest.getMsgObj;
    msg._gateWay = true;
    return [msg, null];
} else if (msg.payload.code === 0) {
    msg.loginByOtp = true;
    msg.quit = true;
    msg.userData = msg.payload.data;
    msg.userData.custId = msg.systalk.needInfo.personalId;
    msg.userData.birthday = msg.systalk.needInfo.birthday;
    return [null, msg]
} else {
    let errorMessage = '';
    // 若 gateway 第一句有回答錯誤訊息
    if (msg.messages.data.content.length && msg.messages.data.content[0].type === 1) {
        // 取出錯誤訊息
        errorMessage = msg.messages.data.content[0].text;
        // 因已取出訊息故將其刪除
        msg.messages.data.content.shift();
    }

    msg.messages.data.content.push({
        type: 8,
        widget: 'otp',
        error: errorMessage // 將錯誤訊息放入 error
    });
    return [msg, null]
}


