msg.reqMsg = msg.payload;
msg.systalk._session_id = msg.reqMsg.sessionId;
msg._start_setup = true;
return msg;

const BotUtils = msg.systalk.utils.BotUtils;
const QueueUtils = msg.systalk.utils.QueueUtils;
const ReqMsgCtrl = msg.systalk.utils.ReqMsgCtrl;
const LangCtrl = msg.systalk.utils.LangCtrl;
const ReqChannels = msg.systalk.utils.ReqChannels;
const ConfigCtrl = msg.systalk.utils.ConfigCtrl;

// normalize request message
// ReqMsgCtrl.normalize(msg.reqMsg);

if (ReqMsgCtrl.isInitSession(msg.reqMsg)) {
    BotUtils.resetSessionStats();
}

if (!msg.systalk.needInfo) {
    msg.systalk.needInfo = {
        lastPayload: null,
        dontKnowTimes: null,
        billMonth:null
    };
}

// messages初始化
msg.messages = {};

// 進線 channel
msg.systalk.channel = node._.clone(msg.reqMsg.channel || ReqChannels.text);

// 使用者語系
msg.systalk.lang = LangCtrl.normalize(msg.reqMsg.lang || msg.systalk.lang || 'zh-tw');

// 傳入 second bot，second bot 判斷與先前若不相同，表示新對話，需重設其已紀錄的資訊
msg.systalk.sessionSalt = msg.systalk.sessionSalt; // || BotUtils.generateSalt();
msg.systalk.fromBot = msg.config.botType;

msg.systalk.pendingBotQueue = Array.isArray(msg.systalk.pendingBotQueue) && msg.systalk.pendingBotQueue.length
    ? msg.systalk.pendingBotQueue
    : [ msg.config.botType ];

// define calling second bot's type
msg.botType = msg.reqMsg.botType || QueueUtils.last(msg.systalk.pendingBotQueue) || null; 
msg.botIntent = msg.reqMsg.intent || msg.systalk.pendingIntent || null;
msg.botEntities = msg.reqMsg.entities || msg.systalk.pendingEntities || null;

msg.systalk.faqState = msg.systalk.faqState || null;
msg.systalk.cannotRecognizedCounter = msg.systalk.cannotRecognizedCounter || 0;
msg.systalk.resendCounter = msg.systalk.resendCounter || 0;

// 設定是否需進入核身流程
msg.systalk.authenticateUser = msg.systalk.authenticateUser || null;


msg.systalk.stored = msg.systalk.stored || {
    // 判斷使用者是否通過核身驗證
     auth: {
         otp: null,
         bank: null
     },
     userData: {
        CUSTIO: null,
        RESPCODE: null,
        CHINAME: null,
        SEX: null
    }
};

// 暫存的身份資訊
msg.systalk.identity = msg.systalk.identity || null;
if (msg.systalk.waitForAuth && msg.reqMsg.message.identity) {
    msg.systalk.identity = msg.reqMsg.message.identity;
    msg.systalk.waitForAuth = null;
}

// 暫存的定位資訊
msg.systalk.location = msg.systalk.location || null;
if (msg.systalk.waitForLocated && msg.reqMsg.message.location) {
    msg.systalk.location = msg.reqMsg.message.location;
    msg.systalk.waitForLocated = null;
}

// 設定值
msg.systalk.defaultConfig = msg.systalk.defaultConfig || {
    faqApi: ConfigCtrl.getFAQApi(msg.config.names.firstFAQ),
    botName: msg.config.defaultConfig.botName
};

msg.systalk.config = msg.systalk.config || node._.cloneDeep(msg.systalk.defaultConfig);
msg.systalk.config.faqApi = msg.systalk.config.faqApi || msg.systalk.defaultConfig.faqApi;
msg.systalk.config.botName = msg.systalk.config.botName || msg.systalk.defaultConfig.botName;

// 用於 report，計數 session 訊息數
msg.systalk.sessionMsgCounter = msg.systalk.sessionMsgCounter ?
(msg.systalk.sessionMsgCounter + 1) : 1;

return msg;

