/**
 * Request message types
 * 總機與各 bot 的 Request 訊息類別
 */
class ReqMsgTypes {
    constructor() {
        this._text = 1;
        this._textType = 'text';
        this._speech = 2;
        this._speechType = 'speech';
        this._fromOnlineService = 3;
        this._fromOnlineServiceType = 'from_online_service';
        this._fromAuthorization = 4;
        this._fromAuthorizationType = 'from_authorization';
        this._fromLocation = 5;
        this._fromLocationType = 'from_location';
        this._signal = 6;
        this._signalType = 'signal';
        this._config = 7;
        this._configType = 'config';
        this._grade = 8;
        this._gradeType = 'grade';
        this._initSession = 9;
        this._initSessionType = 'init_session';
    }
    get text() { return this._text; }
    get textType() { return this._textType }
    get speech() { return this._speech; }
    get speechType() { return this._speechType; }
    get fromOnlineService() { return this._fromOnlineService; }
    get fromOnlineServiceType() { return this._fromOnlineServiceType; }
    get fromAuthorization() { return this._fromAuthorization; }
    get fromAuthorizationType() { return this._fromAuthorizationType; }
    get fromLocation() { return this._fromLocation; }
    get fromLocationType() { return this._fromLocationType; }
    get signal() { return this._signal; }
    get signalType() { return this._signalType; }
    get config() { return this._config; }
    get configType() { return this._configType; }
    get grade() { return this._grade; }
    get gradeType() { return this._gradeType; }
    get initSession() { return this._initSession; }
    get initSessionType() { return this._initSessionType; }

    isText(val) {
        return val === this.text || val === this.textType;
    }
    isSpeech(val) {
        return val === this.speech || val === this.speechType;
    }
    isFromOnlineService(val) {
        return val === this.fromOnlineService || val === this.fromOnlineServiceType;
    }
    isFromAuthorization(val) {
        return val === this.fromAuthorization || val === this.fromAuthorizationType;
    }
    isFromLocation(val) {
        return val === this.fromLocation || val === this.fromLocationType;
    }
    isSignal(val) {
        return val === this.signal || val === this.signalType;
    }
    isConfig(val) {
        return val === this.config || val === this.configType;
    }
    isGrade(val) {
        return val === this.grade || val === this.gradeType;
    }
    isInitSession(val) {
        return val === this.initSession || val === this.initSessionType;
    }
}

