    }

    isInitSession(msg) {
        return !!msg && msg.message ? reqMsgTypes.isInitSession(msg.message.type) : reqMsgTypes.isInitSession(msg.type);
    }

    /**
     * Normalize message's channel
     * By default, channel would be a text channel
     * @param {any} reqMsg request message object
     */
    normalize(reqMsg) {
        if (!reqMsg || !reqMsg.message) {
            return;
        }
        let channelObj = this._normalizeChannel(reqMsg.channel) || reqChannels.text;
        if (!node._.isNil(channelObj)) {
            reqMsg.channel = node._.clone(channelObj);
        }
    }


    /**
     * Normalize channel object
     * @param {any} channel 
     */
    _normalizeChannel(channel) {
        let channelObj = null; // set the default channel
        if (node._.isNumber(channel)) {
            // If the channel is number, and > 0
            // it would be deprecated channel type, convert it.
            if (channel > 0) {
                channelObj = new Channel(channel.toString(), {
                    isText: channel & 1 === 1,
                    isSpeech: channel & 2 === 2
                });
            }
            else {
                channelObj = new Channel(channel.toString(), {
                    isText: true,
                    isSpeech: false
                });
            }
        }
        else if (node._.isString(channel)) {
            channelObj = new Channel(channel, {
                isText: true,
                isSpeech: false
            });
        }
        else if (!node._.isNil(channel) &&
            node._.isString(channel.name)) {
            channelObj = new Channel(channel.name, {
                isText: node._.isBoolean(channel.isText) ? channel.isText : !!channel.isText,
                isSpeech: node._.isBoolean(channel.isSpeech) ? channel.isSpeech : !!channel.isSpeech
            });
        }
        return channelObj;
    }
}

