/**
 * 物件相關 utils
 */
class ObjUtils {

    removeSpecialCharFromKeys(obj) {
        for (let prop in obj) {
            let testReg = /\./g;
            if (testReg.test(prop)) {
                const newProp = prop.replace(testReg, '');
                let propVal = obj[prop];
                delete obj[prop];
                obj[newProp] = propVal;
            }
        }
        return obj;
    }
}

/**
 * 訊息相關 utils
 */
class MsgUtils {
    /**
     * Create output array (index of total)
     * @params msg msg obj
     * @params index 1-based
     * @params total num of outputs
     */
    output(msg, index, total) {
        total = !isNaN(total) && total >= 1 ? total : 1;
        index = !isNaN(index) && index >= 1 && index <= total ? index : 1;
        let outputs = new Array(total);
        outputs.fill(null);
        outputs[index - 1] = msg;
        return outputs;
    }
}

